import Header from './Header';
import Content from './Content';
import Footer from './Footer';
import AddBooks from './AddItem';
import './App.css';

import { useState } from "react";

const App = () => {

  const [books, setBooks] = useState([
    {
      id: 1,
      BookName: "PHP 8",
      YearPublished: "2023",
      Author: "VicS",
      Category: "Web",
      status: 1,
    },
    {
      id: 2,
      BookName: "React.js",
      YearPublished: "2000",
      Author: "Peter SMith",
      Category: "Web",
      status: 1,

    },
    {
      id: 3,
      BookName: "CSS framework",
      YearPublished: "2005",
      Author: "Jaguar",
      Category: "Web",
      status: 1,
    },
    {
      id: 4,
      BookName: "Data Science",
      YearPublished: "2023",
      Author: "Vic S",
      Category: "Data",
      status: 1,
  },

]);

  const [newItem, setNewItem] = useState('')

  //new item 
  const handleSumbit = (e)=>{
   e.preventDefault(); //preventing the post page

   console.log('Inserted: ' + newBooks)

   const idx = Books.length ? Books[Books.length - 1 ].id + 1 : 1; //identifying the last id created
   /* 
    if(items.length > 0){
      //items.length = 3
      //3 - 1 
      //2
      //[2].id
     idx = items[items.length - 1].id + 1  //4
    } else {
      idx = 1
    }*/

   console.log(idx);
   const myNewBooks = { 
                      id:idx, 
                      BookName: newBooks, 
                      itemName:newItem,
                     };

   const listBooks = [...items, myNewBooks]; //adding an element into an arrays of obejcts, using spread operator
   setItems(listItems);
   setNewItem('');
  }

  const handleCheckBox = (id)=>{
  const listBooks = books.map( (books) => books.id === id ? { ...books, checked: !books.checked } : books )
  setItems(listItems);
  }

  const handleDelete = (id)=> {
  console.log('test delete ' + id)
  const listItems = items.filter( (books) => books.id !== id  )
  setBooks(listBooks)
  }


  let average = 100 / 5;

  return (

    <div className="App">

      <Header 
      title="WD74P" 
      description="Header for React CRUD (create, read, update and delete) app example" 
      average={average} 
      />

      <AddItem
      handleSumbit = {handleSumbit}
      newItem = {newItem} 
      setNewItem = {setNewItem}
      />


      <Content 
      items = {items} 
      handleCheckBox = {handleCheckBox}
      handleDelete = {handleDelete}
      />

      <Footer />

    </div>

  );
}

export default App;
