    {
      id: 3,
      BookName: "CSS framework",
      YearPublished: "2005",
      Author: "Jaguar",
      Category: "Web",
      status: 1,
    },
    {
      id: 4,
      BookName: "Data Science",
      YearPublished: "2023",
      Author: "Vic S",
      Category: "Data",
      status: 1,
  },

]);

  return (

    <div className="App">

      <Header 
      title="WD74P" 
      description="Header for React CRUD (create, read, update and delete) app example" 

      />

      <AddBooks/>


      <Content 
      books = {books} 
      />

      <Footer />

    </div>


  );
};

export default App
